mapper.select:
SELECT dvc_list
FROM aircok.user_tb
WHERE user_id = #{id}

SELECT 
	data_reg_dt, iaq,

        COALESCE(
        	CASE WHEN LENGTH(${sensor}) = 0
	        	THEN NULL
	        	ELSE ${sensor}
       		END, '0') as ${sensor}

FROM aircok_device.dvc_${sn}

WHERE data_reg_dt > #{stime} AND data_reg_dt < #{etime}

ORDER BY data_reg_dt;
